import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useAppContext } from '../global.context';

const Detail = () => {
  const { state } = useAppContext();
  const { id } = useParams();
  const [dentist, setDentist] = useState(null);

  useEffect(() => {
    // Realiza una llamada a la API para obtener los detalles del dentista específico por ID
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setDentist(data);
      })
      .catch((error) => {
        console.error('Error al obtener detalles del dentista:', error);
      });
  }, [id]);

  return (
    <div className={`theme-${state.theme}`}>
      {dentist ? (
        <>
          <h1>Detalle del Dentista</h1>
          <h2>Nombre: {dentist.name}</h2>
          <p>Email: {dentist.email}</p>
          <p>Teléfono: {dentist.phone}</p>
          <p>Sitio web: {dentist.website}</p>
        </>
      ) : (
        <p>Cargando detalles del dentista...</p>
      )}
    </div>
  );
};

export default Detail;
