import React, { useState } from 'react';
import { GlobalContext } from '../utilsglobal.context';
import Form from '../Components/Form';

const Contact = () => {
  const { state } = React.useContext(GlobalContext);
  const [formData, setFormData] = useState({ name: '', email: '' });
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validaciones
    if (formData.name.length <= 5 || !isValidEmail(formData.email)) {
      setError('Por favor verifique su información nuevamente');
    } else {
      setSuccessMessage(`Gracias ${formData.name}, te contactaremos cuando antes vía mail`);
      setError(null);
      // Aquí puedes enviar los datos a un servidor o realizar acciones adicionales
    }
  };

  const isValidEmail = (email) => {
    // Validación de formato de email
    const emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;
    return emailRegex.test(email);
  };

  return (
    <div className={`theme-${state.theme}`}>
      <h2>¿Quieres saber más?</h2>
      <p>Envíanos tus preguntas y te contactaremos</p>
      <Form
        formData={formData}
        onInputChange={handleInputChange}
        onSubmit={handleSubmit}
        error={error}
        successMessage={successMessage}
      />
    </div>
  );
};

export default Contact;
