import React from "react";
import { Link } from "react-router-dom";
import { GlobalContext } from './components/global.context';
/*useAppContext*/
const Card = ({ dentist }) => {
  const { state, dispatch } = React.useContext(GlobalContext);
  const { name, username, id } = dentist;

  const toggleFav = () => {
    // Verificar si la tarjeta ya está en favoritos
    const isFav = state.favs.some((fav) => fav.id === id);

    if (isFav) {
      // Si está en favoritos, quitarla
      dispatch({ type: "REMOVE_FAV", payload: id });
    } else {
      // Si no está en favoritos, agregarla
      dispatch({ type: "ADD_FAV", payload: dentist });
    }
  };

  return (
    <div className="card">
      <h2>{name}</h2>
      <p>@{username}</p>
      <Link to={`/dentist/${id}`} className="card-link">
        Detalles
      </Link>
      <button onClick={toggleFav} className="favButton">
        {state.favs.some((fav) => fav.id === id) ? "Quitar fav" : "Add fav"}
      </button>
    </div>
  );
};

export default Card;
