import React, { useEffect } from 'react';
import { GlobalContext } from '../components/utils/global.context'; // Asegúrate de importar el contexto adecuadamente
import Card from '../Components/Card';
;

const Home = () => {
  const { state, setData, dispatch } = React.useContext();

  useEffect(() => {
    // Realiza una llamada a la API y actualiza el estado global con los datos de los dentistas
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        console.error('Error al obtener datos de la API:', error);
      });
  }, [setData]);

  const toggleFav = (dentist) => {
    // Agrega o quita un dentista de la lista de destacados en el estado global
    if (state.favs.some((fav) => fav.id === dentist.id)) {
      dispatch({ type: 'REMOVE_FAV', payload: dentist.id });
    } else {
      dispatch({ type: 'ADD_FAV', payload: dentist });
    }
  };

  return (
    <main className={`theme-${state.theme}`}>
      <h1>Home</h1>
      <div className='card-grid'>
        {state.data.map((dentist) => (
          <Card key={dentist.id} dentist={dentist} onToggleFav={toggleFav} />
        ))}
      </div>
    </main>
  );
};

export default Home;
